import Combine
import CombineExt

final class UserFormWireframe: BaseWireframe<UserFormViewController> {

    // MARK: - Private properties -

    private let storyboard = UIStoryboard.userForm

    private let userActionRelay: PassthroughRelay<UserForm.Action>

    // MARK: - Module setup -

    init(score: Float, userActionRelay: PassthroughRelay<UserForm.Action>) {
        let moduleViewController = storyboard.instantiateViewController(ofType: UserFormViewController.self)
        self.userActionRelay = userActionRelay

        super.init(viewController: moduleViewController)

        let interactor = UserFormInteractor(input: .init(score: score, highscoreService: HighscoreService()))
